import React, { useState, useRef } from "react";
import axios from "axios";
import UploadComponent from "./components/UploadComponent";
import PdfViewerComponent from "./components/PdfViewerComponent";

// Function to apply basic syntax highlighting to JSON (version 1)
const syntaxHighlight = (json) => {
    if (typeof json !== "string") {
        json = JSON.stringify(json, undefined, 2);
    }
    json = json
        .replace(/&/g, "&amp;")
        .replace(/</g, "&lt;")
        .replace(/>/g, "&gt;");
    return json.replace(
        /("(\\u[a-zA-Z0-9]{4}|\\[^u]|[^\\"])*"(\s*:)?|\b(true|false|null)\b|\b-?\d+(?:\.\d*)?(?:[eE][+-]?\d+)?\b)/g,
        (match) => {
            let cls = "number";
            if (/^"/.test(match)) {
                if (/:$/.test(match)) {
                    cls = "key";
                } else {
                    cls = "string";
                }
            } else if (/true|false/.test(match)) {
                cls = "boolean";
            } else if (/null/.test(match)) {
                cls = "null";
            }
            return `<span class="${cls}">${match}</span>`;
        }
    );
};

function App() {
    const [jsonSchema, setJsonSchema] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [pdfFile, setPdfFile] = useState(null);
    const [jsonEditValue, setJsonEditValue] = useState("");
    const jsonDivRef = useRef(null); // Reference for JSON div

    // Handle the file upload
    const onDrop = async (acceptedFiles) => {
        if (acceptedFiles.length === 0) return;

        setIsLoading(true);
        setPdfFile(acceptedFiles[0]); // Set the uploaded PDF file for visualization

        const formData = new FormData();
        formData.append("pdf", acceptedFiles[0]);

        try {
            const response = await axios.post("http://localhost:5001/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            // Update the state with the received JSON schema
            if (response.data && response.data.schema) {
                const schema = response.data.schema;
                setJsonSchema(schema); // Set the schema
                setJsonEditValue(JSON.stringify(schema, null, 2)); // Update the text area value for editing
            }
        } catch (error) {
            console.error("Error uploading PDF:", error);
        } finally {
            setIsLoading(false);
        }
    };

    // Handle changes to the editable JSON text area
    const handleJsonEditChange = (event) => {
        const selection = window.getSelection();
        const range = selection.getRangeAt(0);
        const startOffset = range.startOffset;

        setJsonEditValue(event.target.textContent);

        // Restore cursor position after updating the content
        setTimeout(() => {
            if (jsonDivRef.current) {
                const newRange = document.createRange();
                newRange.setStart(jsonDivRef.current.childNodes[0], startOffset);
                newRange.collapse(true);
                selection.removeAllRanges();
                selection.addRange(newRange);
            }
        }, 0);
    };

    // Function to download the JSON schema
    const downloadJson = () => {
        if (!jsonSchema) return;

        // Create a Blob with the JSON data
        const jsonString = JSON.stringify(jsonSchema, null, 2); // Pretty print JSON
        const blob = new Blob([jsonString], { type: "application/json" });

        // Create an anchor element and trigger a download
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.download = "generated_schema.json"; // The filename for the downloaded file
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* Left Side - Upload PDF & PDF Viewer */}
            <div style={{ width: "50%", display: "flex", flexDirection: "column", padding: '20px', borderRight: '1px solid #ccc' }}>
                {/* Upload Component */}
                <UploadComponent onDrop={onDrop} isLoading={isLoading} pdfFile={pdfFile} />

                {/* PDF Viewer */}
                {pdfFile && <PdfViewerComponent pdfFile={pdfFile} style={{ marginTop: "20px" }} />}
            </div>

            {/* Right Side - Highlighted JSON Schema Editor */}
            <div style={{ width: "50%", padding: '20px', display: 'flex', flexDirection: 'column' }}>
                {jsonSchema && (
                    <>
                        {/* Title for JSON Schema */}
                        <h3 style={{ color: "#000000", marginBottom: "10px" }}>Highlighted JSON Schema Preview</h3>

                        {/* Highlighted JSON Schema */}
                        <div
                            ref={jsonDivRef}
                            contentEditable
                            onInput={handleJsonEditChange}
                            dangerouslySetInnerHTML={{ __html: syntaxHighlight(jsonEditValue) }}
                            style={{
                                width: "100%",
                                flex: 1,
                                padding: "15px",
                                fontFamily: "monospace",
                                fontSize: "14px",
                                border: "1px solid #ccc",
                                borderRadius: "4px",
                                backgroundColor: "#2b2b2b",
                                color: "#ffffff",
                                overflow: "auto",
                                whiteSpace: "pre",
                                outline: "none",
                                marginBottom: "10px",
                                resize: "vertical", // Allows resizing vertically
                            }}
                        ></div>

                        {/* Button Container */}
                        <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                            {/* Save Edited JSON Button */}
                            <button
                                onClick={() => {
                                    try {
                                        const parsedJson = JSON.parse(jsonEditValue);
                                        setJsonSchema(parsedJson);
                                        alert("JSON updated successfully!");
                                    } catch (error) {
                                        alert("Invalid JSON format. Please correct it before saving.");
                                    }
                                }}
                                style={{
                                    padding: "10px",
                                    backgroundColor: "#007bff",
                                    color: "#fff",
                                    border: "none",
                                    borderRadius: "4px",
                                    cursor: "pointer",
                                }}
                            >
                                Save Edited JSON
                            </button>

                            {/* Download JSON Button */}
                            <button
                                onClick={downloadJson}
                                style={{
                                    padding: "10px",
                                    backgroundColor: "#28a745",
                                    color: "#fff",
                                    border: "none",
                                    borderRadius: "4px",
                                    cursor: "pointer",
                                }}
                            >
                                Download JSON
                            </button>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
}

export default App;
