import React, { useState } from "react";
import axios from "axios";
import UploadComponent from "./components/UploadComponent";
import JsonDisplayComponent from "./components/JsonDisplayComponent";

function App() {
    const [jsonSchema, setJsonSchema] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    const onDrop = async (acceptedFiles) => {
        if (acceptedFiles.length === 0) return;

        setIsLoading(true);
        const formData = new FormData();
        formData.append("pdf", acceptedFiles[0]);

        try {
            const response = await axios.post("http://localhost:5000/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });
            setJsonSchema(response.data);
        } catch (error) {
            console.error("Error uploading PDF:", error);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* Left Side - Upload PDF */}
            <UploadComponent onDrop={onDrop} isLoading={isLoading} />

            {/* Right Side - JSON Schema Display */}
            <JsonDisplayComponent jsonSchema={jsonSchema} />
        </div>
    );
}

export default App;
