import React, { useState } from "react";
import axios from "axios";
import UploadComponent from "./components/UploadComponent";
import PdfViewerComponent from "./components/PdfViewerComponent";

function App() {
    const [jsonSchema, setJsonSchema] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [pdfFile, setPdfFile] = useState(null);
    const [jsonEditValue, setJsonEditValue] = useState("");

    // Handle the file upload
    const onDrop = async (acceptedFiles) => {
        if (acceptedFiles.length === 0) return;

        setIsLoading(true);
        setPdfFile(acceptedFiles[0]); // Set the uploaded PDF file for visualization

        const formData = new FormData();
        formData.append("pdf", acceptedFiles[0]);

        try {
            const response = await axios.post("http://localhost:5001/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            // Update the state with the received JSON schema
            if (response.data && response.data.schema) {
                const schema = response.data.schema;
                setJsonSchema(schema); // Set the schema
                setJsonEditValue(JSON.stringify(schema, null, 2)); // Update the text area value for editing
            }
        } catch (error) {
            console.error("Error uploading PDF:", error);
        } finally {
            setIsLoading(false);
        }
    };

    // Handle changes to the editable JSON text area
    const handleJsonEditChange = (event) => {
        setJsonEditValue(event.target.value);
    };

    // Validate and save the edited JSON
    const saveEditedJson = () => {
        try {
            const parsedJson = JSON.parse(jsonEditValue);
            setJsonSchema(parsedJson);
            alert("JSON updated successfully!");
        } catch (error) {
            alert("Invalid JSON format. Please correct it before saving.");
        }
    };

    // Function to download the JSON schema
    const downloadJson = () => {
        if (!jsonSchema) return;

        // Create a Blob with the JSON data
        const jsonString = JSON.stringify(jsonSchema, null, 2); // Pretty print JSON
        const blob = new Blob([jsonString], { type: "application/json" });

        // Create an anchor element and trigger a download
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.download = "generated_schema.json"; // The filename for the downloaded file
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* Left Side - Upload PDF & PDF Viewer */}
            <div style={{ flex: 1, display: "flex", flexDirection: "column", padding: '20px', borderRight: '1px solid #ccc' }}>
                {/* Upload Component */}
                <UploadComponent onDrop={onDrop} isLoading={isLoading} pdfFile={pdfFile} />

                {/* PDF Viewer */}
                {pdfFile && <PdfViewerComponent pdfFile={pdfFile} style={{ marginTop: "20px" }} />}
            </div>

            {/* Right Side - JSON Schema Display and Editing */}
            <div style={{ flex: 1, padding: '20px', display: 'flex', flexDirection: 'column' }}>
                {jsonSchema && (
                    <>
                        <h3 style={{ color: "#ffffff" }}>Editable JSON Schema:</h3>
                        <textarea
                            value={jsonEditValue}
                            onChange={handleJsonEditChange}
                            style={{
                                width: "100%",
                                flex: 1,
                                padding: "15px",
                                fontFamily: "monospace",
                                fontSize: "14px",
                                border: "1px solid #ccc",
                                borderRadius: "4px",
                                backgroundColor: "#2b2b2b",
                                color: "#ffffff",
                                resize: "vertical", // Allows resizing vertically
                                marginBottom: "10px",
                            }}
                        ></textarea>

                        {/* Button Container */}
                        <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                            {/* Save Edited JSON Button */}
                            <button
                                onClick={saveEditedJson}
                                style={{
                                    padding: "10px",
                                    backgroundColor: "#007bff",
                                    color: "#fff",
                                    border: "none",
                                    borderRadius: "4px",
                                    cursor: "pointer",
                                }}
                            >
                                Save Edited JSON
                            </button>

                            {/* Download JSON Button */}
                            <button
                                onClick={downloadJson}
                                style={{
                                    padding: "10px",
                                    backgroundColor: "#28a745",
                                    color: "#fff",
                                    border: "none",
                                    borderRadius: "4px",
                                    cursor: "pointer",
                                }}
                            >
                                Download JSON
                            </button>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
}

export default App;
